import React, { useState } from "react";
import Button from "@mui/material/Button";
import Drawer from "@mui/material/Drawer";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import alertify from "alertifyjs";
import axios from "axios";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { useNavigate, useParams } from 'react-router-dom'
import { useEffect } from 'react'
import { savedEmployee, updateDataEmployee, editEmployee } from '../service/EmployeeService'

function AccountForm({ onSaveAccount, open = true, onClose, actions }) {
    const [userData, setUserData] = useState({
      accountName: "",
      accountType: "",
    });

    // const handleInputChange = (event) => {
    //   const { name, value } = event.target;
    //   console.log('hooooooooooooooooooooo');
    //   setUserData({
    //     ...userData,
    //     [name]: value,
    //   });
    // };

    // const handleSaveUser = async (event) => {
    //   event.preventDefault();

    //   console.log('hooooooooooooooooooooo');

    //   // const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    //   // const accessToken = userInfo.access_token;

    //   const jsonData = {};

    //   jsonData["user_name"] = "userData.user_name";
    //   jsonData["full_name"] = "userData.full_name";
    //   jsonData["password"] = "userData.password";
    //   jsonData["status"] = "userData.status";
    //   jsonData["add_by"] = 1;
    //   jsonData["add_date"] = Date.now();



    //   console.log(jsonData);
    //   // console.log(accessToken);

    //   try {
    //     const apiUrl = "http://localhost:7080/users";

    //     // const response = await axios.post(apiUrl, jsonData, {
    //     //   headers: {
    //     //     "Content-Type": "application/json",
    //     //   },
    //     // });
    //     const response = axios.post(apiUrl, { jsonData });
    //     if (response.status === 201) {
    //       alertify.success("تم اضافة الحساب.");
    //     }
    //   } catch (error) {
    //     alertify.success(error.stack);
    //     alertify.success(error.name);

    //     alertify.success(error.message);

    //     console.log(error);
    //     alertify.error("Something went wrong");    }

    //   actions.getAccounts();

    //   onSaveAccount(userData);
    //   onClose();
    // };


  const [firstName, setFirstName] = useState('')
  const [lastName, setLastName] = useState('')
  const [email, setEmail] = useState('')

  const navigate = useNavigate()
  const { id } = useParams()


  function pageTitle() {
    if (id) {
      return <h4 className='title'>Update Employees</h4>
    } else {
      return <h4 className='title'>Add Employees</h4>
    }
  }

  useEffect(() => {
    if (id) {
      editEmployee(id).then((response) => {
        setFirstName(response.data.firstName);
        setLastName(response.data.lastName);
        setEmail(response.data.email);
      })
    }
  }, [id])

  function saveEmployee(e) {
    e.preventDefault()

    const employee = { firstName, lastName, email }
    alertify.success(" succesfull.");
    alertify.success();
    if (firstName === "" || lastName === "" || email === "") {
      return;
    }
    if (id) {
      updateDataEmployee(id, employee).then((response) => {
        navigate('/')
      }).catch(error => {
        console.error(error);
      })
    } else {
      savedEmployee(employee).then((response) => {
      }).catch(error => {
        console.error(error);
      })
      navigate("/")
    }
  }

  return (
    <Drawer
      anchor="left"
      open={open}
      onClose={onClose}
      ModalProps={{
        disableScrollLock: true,
      }}
    >
      <div dir="rtl"
        style={{
          width: "300px",
          padding: "16px",
        }}
        role="presentation"
        onClick={onClose}
        onKeyDown={onClose}
      >
        <TextField dir="rtl"
          style={{ marginTop: "60px" }}
          name="user_name"
          label="اسم المستخدم "
          type="text"
          placeholder='Enter FirstName'
          value={firstName}
          className='form-control'
          onChange={(e) => setFirstName(e.target.value)}
          id="validationCustom01"
          required
          fullWidth
          margin="normal"
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {

            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          name="full_name"
          label="الاسم الكامل"
          // value="abdullah"
          fullWidth
          onChange={(e)=>setLastName(e.target.value)}
          margin="normal"
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          name="password"
          label="كلمة المرور"
          value="abdullah"
          fullWidth
          margin="normal"
          onChange={(e)=>setEmail(e.target.value)}
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          type="checkbox"
          name="status"
          label="الحالة"
          value="1"
          // onChange={handleInputChange}
          fullWidth
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />


        <Box mt={2}>
          <Button
            variant="contained"
            color="primary"
            onClick={saveEmployee}
            sx={{ backgroundColor: "#EB3D13" }}>
            حفظ
          </Button>
        </Box>
      </div>
    </Drawer>
  );
}

export default connect(null)(AccountForm);
