import React, { useState } from "react";
import Button from "@mui/material/Button";
import Drawer from "@mui/material/Drawer";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import alertify from "alertifyjs";
import axios from "axios";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import * as accountActions from "../../redux/actions/accountActions";

function AccountForm({ onSaveAccount, open = true, onClose, actions }) {
  const [userData, setUserData] = useState({
    accountName: "",
    accountType: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    console.log('hooooooooooooooooooooo');
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  const handleSaveUser = async (event) => {
    event.preventDefault();
    console.log('hooooooooooooooooooooo');

    // const userInfo = JSON.parse(localStorage.getItem("userInfo"));
    // const accessToken = userInfo.access_token;

    const jsonData = {};

    jsonData["user_name"] = userData.user_name;
    jsonData["full_name"] = userData.full_name;
    jsonData["password"] = userData.password;
    jsonData["status"] = userData.status;
    jsonData["add_by"] = userInfo.id;
    jsonData["add_date"] = Date.now();



    console.log(jsonData);
    console.log(accessToken);

    try {
      const apiUrl = "http://127.0.0.1:7080/users";
      const response = await axios.post(apiUrl, jsonData, {
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer: " + accessToken, // JSON verisi göndermek için content type ayarı
        },
      });

      if (response.status === 201) {
        alertify.success("New account added.");
      }
    } catch (error) {
      console.log(error);
      alertify.error("Something went wrong");    }

    actions.getAccounts();

    onSaveAccount(userData);
    onClose(); // Yan menüyü kapat
  };

  return (
    <Drawer
      anchor="left"
      open={open}
      onClose={onClose}
      ModalProps={{
        disableScrollLock: true,
      }}
    >
      <div dir="rtl"
        style={{
          width: "300px",
          padding: "16px",
        }}
        role="presentation"
        onClick={onClose}
        onKeyDown={onClose}
      >
        <TextField dir="rtl"
          style={{ marginTop: "60px" }}
          name="user_name"
          label="اسم المستخدم"
          value="abdullah"
          fullWidth
          onChange={handleInputChange}
          margin="normal"
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {

            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          name="full_name"
          label="الاسم الكامل"
          // value="abdullah"
          fullWidth
          onChange={handleInputChange}
          margin="normal"
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          name="password"
          label="كلمة المرور"
          value="abdullah"
          fullWidth
          margin="normal"
          onChange={handleInputChange}
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          type="checkbox"
          name="status"
          label="الحالة"
          value="1"
          onChange={handleInputChange}
          fullWidth
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />


        <Box mt={2}>
          <Button
            variant="contained"
            color="primary"
            onClick={handleSaveUser}
            sx={{ backgroundColor: "#EB3D13" }}
          >
            حفظ
          </Button>
        </Box>
      </div>
    </Drawer>
  );
}

function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getAccounts: bindActionCreators(accountActions.getAccounts, dispatch)
    },
  };
}

export default connect(null, mapDispatchToProps)(AccountForm);
