import * as React from "react";
import Link from "@mui/material/Link";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Title from "./Title";
import axios from "axios";
import { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as accountActions from "../../redux/actions/accountActions";
import Button from "@mui/material/Button";
import { listUsers, deleteUser } from '../service/UserService.js'
import { useNavigate } from 'react-router-dom'
import alertify from "alertifyjs";

function preventDefault(event) {
  event.preventDefault();
}

function Accounts(props) {
  const [isFormOpen, setIsFormOpen] = useState(false);

  const handleSaveUser = (accountInfo) => {
    console.log("Yeni hesap bilgileri:", accountInfo);
  };


  useEffect(() => {
    props.actions.getAccounts();
  }, [props.actions]);

  function dateFormatter(dateArray) {
    const year = dateArray[0];
    const month = dateArray[1];
    const day = dateArray[2];

    return year + " : " + month + " : " + day;
  }


  const navigate = useNavigate();

    const [user, setUser] = useState([])

    useEffect(() => {
      getAllUser()
    }, [])

    function getAllUser() {
      listUsers().then((response) => {

    setUser(response.data)
        }).catch(error => {

            console.error(error);
        })
    }
  return (
    <React.Fragment>
      <Title>المستخدمين {props.currentAccount.account_name}</Title>
      <Table size="small">
        <TableHead>
          <TableRow>
            <TableCell>User Id</TableCell>
            <TableCell>User Name</TableCell>
            <TableCell>full Number</TableCell>

          </TableRow>
        </TableHead>
        <TableBody>
          {user.map((account) => (
            <TableRow
              onClick={() => {
                props.actions.changeAccount(account);
              }}
              key={account.account_id}
            >

              <TableCell>{account.account_id}</TableCell>
              <TableCell>{account.account_name}</TableCell>
              <TableCell>{account.account_number}</TableCell>
              <TableCell>{account.account_type}</TableCell>
              <TableCell>{account.user_id}</TableCell>
              <TableCell>{dateFormatter(account.updated_at)}</TableCell>
              <TableCell>{`$${account.balance}`}</TableCell>
              <TableCell align="right">
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => setIsFormOpen(true)}
                  sx={{ backgroundColor: "#F5BD52" }}
                >
                  Transact
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Link color="primary" href="#" onClick={preventDefault} sx={{ mt: 3 }}>
        رؤية المزيد من المستخدمين
      </Link>
    </React.Fragment>
  );
}

function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getAccounts: bindActionCreators(accountActions.getAccounts, dispatch),
      changeAccount: bindActionCreators(accountActions.changeAccount, dispatch),
    },
  };
}

function mapStateToProps(state) {
  return {
    currentAccount: state.changeAccountReducer,
    accounts: state.accountListReducer,
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Accounts);
