import * as React from "react";
import Link from "@mui/material/Link";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Title from "./Title";
import axios from "axios";
import { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as accountActions from "../../redux/actions/accountActions";
import Button from "@mui/material/Button";
import { listUsers, deleteUser } from '../service/UserService.js'
import { useNavigate } from 'react-router-dom'
import alertify from "alertifyjs";
import Grid from "@mui/material/Grid";
import AddAccount from "./AddAccount";
import Drawer from "./Drawer.js" 
function preventDefault(event) {
  event.preventDefault();
}

function Accounts(props) {
  const [isFormOpen, setIsFormOpen] = useState(false);


  function dateFormatter(dateArray) {
    const year = dateArray[0];
    const month = dateArray[1];
    const day = dateArray[2];

    return year + " : " + month + " : " + day;
  }


//   const navigate = useNavigate();

    const [user, setUser] = useState([])

    useEffect(() => {
      getAllUser()
    }, [])

    function getAllUser() {
      listUsers().then((response) => {
    alertify.success(" succesfull.");

    setUser(response.data)
        }).catch(error => {

            console.error(error);
        })
    }
  return (
    <React.Fragment>
            <Drawer/>

      <Title>المستخدمين </Title>
      <Table size="small">
        <TableHead>
          <TableRow>
            <TableCell> ID</TableCell>
            <TableCell>اسم المستخدم</TableCell>
            <TableCell>الاسم الكامل</TableCell>
            <TableCell>الحالة</TableCell>
            <TableCell>اضيف بواسطة</TableCell>
            <TableCell>تاريخ الاصافة</TableCell>

          </TableRow>
        </TableHead>
        <TableBody>
          {user.map((item) => (
            <TableRow

              key={item.id}
            >

              <TableCell>{item.user_name}</TableCell>
              <TableCell>{item.full_name}</TableCell>
              <TableCell>{item.status}</TableCell>
              <TableCell>{item.add_by}</TableCell>
              <TableCell>{dateFormatter(item.add_date)}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Link color="primary" href="#" onClick={preventDefault} sx={{ mt: 3 }}>
        رؤية المزيد من المستخدمين
      </Link>
      <Grid item xs={12} container justifyContent="flex-end">
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => setIsFormOpen(true)}
                  sx={{ backgroundColor: "#EB3D13" }}
                >
                  اضافة مستخدم
                </Button>
                <AddAccount
                  // onSaveAccount={handleSaveUser}
                  open={isFormOpen}
                  onClose={() => setIsFormOpen(false)}
                />
              </Grid>
    </React.Fragment>
  );
}


export default Accounts;
