import Box from "@mui/material/Box";
import alertify from "alertifyjs";
import { connect } from "react-redux";
import { useNavigate, useParams } from 'react-router-dom'
import { useEffect } from 'react'
import { savedCommitteeData, updateCommitteeData, editCommitteeData } from '../../service/CommitteeDataService'
import Title from "../Title";
import Grid from "@mui/material/Grid";
import { MenuItem, Select } from "@mui/material";
import Divider from "@mui/material/Divider";

function CommitteeDataForm({ onSaveCommitteeData, open = true, onClose, actions }) {


  const [committee_type_id, setcommittee_type_id] = useState('')
  const [partner_id, setpartner_id] = useState('')
  const [list_id, setlist_id] = useState('')
  const [status, setstatus] = useState('')
  const [add_by, setadd_by] = useState('')
  const [add_date, setadd_date] = useState('')


  const navigate = useNavigate()
  const { id } = useParams()


  function pageTitle() {
    if (id) {
      return <h4 className='title'>تعديل</h4>
    } else {
      return <h4 className='title'>انشاء جديد</h4>
    }
  }

  useEffect(() => {
    if (id) {
      editCommitteeData(id).then((response) => {
        setcommittee_type_id(response.data.committee_type_id);
        setpartner_id(response.data.partner_id);
        setlist_id(response.data.list_id);
        setstatus(response.data.status);
        setadd_by(response.data.add_by);
        setadd_date(response.data.add_date);
      })
    }
  }, [id])

  function saveCommitteeData(e) {
    e.preventDefault()
    alertify.success(" start save.");

    const committeeData = {
      member_name, gender, sociality, phone, mobile, id_type, id_number,
      id_date, id_location, birth_place, birthdate, accomm_type, qualification, job_title, workplace, work_type, experience,
      photo, person, person_relation, person_mobile,
    }


    if (job_title === "") {
      return;
    }
    if (id) {
      updateDataCommitteeData(id, committeeData).then((response) => {
        // navigate('/')
      }).catch(error => {
        console.error(error);
      })
    } else {
      savedCommitteeData(committeeData).then((response) => {
      }).catch(error => {
        alertify.success(error.message);

        console.error(error);
      })
      // navigate("/")
    }
  }

  return (
    <Drawer
      anchor="left"
      open={open}
      onClose={onClose}
      ModalProps={{
        disableScrollLock: true,
      }}  >
      <div dir="rtl"
        style={{
          width: "900px",
          padding: "16px",
        }}
        role="presentation"
        onClick={onClose}
        onKeyDown={onClose}    >
        <Title marginTop="60px">اضافة </Title>

        <Grid container spacing={2}>
          <Grid item xs={12} sm={3}>
            <TextField
              variant="outlined"
              fullWidth
              name="member_name"
              label="اسم العضو "
              type="text"
              placeholder='ادخل اسم العضو'
              className='form-control'
              onChange={(e) => setmember_name(e.target.value)}
              required
              margin="normal"
              onClick={(event) => {
                event.stopPropagation();
              }}
              onKeyDown={(event) => {

                event.stopPropagation();
              }}
            />
          </Grid>
          <Grid item xs={12} sm={3}>
            <Select
              name="gender"
              label="الجنس"
              onChange={(e) => setgender(e.target.value)}
              fullWidth
              placeholder="اختر الجنس"
              onClick={(event) => {
                event.stopPropagation();
              }}
              onKeyDown={(event) => {
                event.stopPropagation();
