// import logo from './logo.svg';
// import './App.css';
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import Login from "../login/Login";
import Dashboard from "../dashboard/Dashboard"
import Users from "../dashboard/Users"
import MuiAppBar from "@mui/material/AppBar";
import MuiDrawer from "@mui/material/Drawer";
import IconButton from "@mui/material/IconButton";
import Toolbar from "@mui/material/Toolbar";
import MenuIcon from "@mui/icons-material/Menu";
import Box from "@mui/material/Box";
import CssBaseline from "@mui/material/CssBaseline";
import Divider from "@mui/material/Divider";
import List from "@mui/material/List";
import * as React from "react";
import ListItemButton from '@mui/material/ListItemButton';
import ListItem from '@mui/material/ListItem';

import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import { styled, createTheme, ThemeProvider } from "@mui/material/styles";
import alertify from "alertifyjs";
import DashboardIcon from '@mui/icons-material/Dashboard';
import ShoppingCartIcon from '@mui/icons-material/ShoppingCart';
import PeopleIcon from '@mui/icons-material/People';
import BarChartIcon from '@mui/icons-material/BarChart';
import LayersIcon from '@mui/icons-material/Layers';
import { useNavigate } from "react-router-dom";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";
import { Navigate } from "react-router-dom";
const drawerWidth = 240;

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== "open",
})
  (({ theme, open }) => ({
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
      marginLeft: drawerWidth,
      width: `calc(100% - ${drawerWidth}px)`,
      transition: theme.transitions.create(["width", "margin"], {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.enteringScreen,
      }),
    }),
  }));

const Drawer = styled(MuiDrawer, {
  shouldForwardProp: (prop) => prop !== "open",
})(({ theme, open }) => ({
  "& .MuiDrawer-paper": {
    position: "relative",
    whiteSpace: "nowrap",
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),

    boxSizing: "border-box",
    ...(!open && {
      overflowX: "hidden",
      transition: theme.transitions.create("width", {
        easing: theme.transitions.easing.sharp,
        duration: theme.transitions.duration.leavingScreen,
      }),
      width: theme.spacing(7),
      [theme.breakpoints.up("sm")]: {
        width: theme.spacing(9),
      },
    }),
  },
}));

const defaultTheme = createTheme({
  direction: 'rtl',
});


const userRoute = () => {


  Navigate('/Users');
}
const SystemConstants = async (event) => {

  return <Navigate to="/login" />
  alertify.success("Login succesfull.")
  Navigate('/Users')



};
function App() {

  return (
    <div>
      <BrowserRouter className="App" id="light">

        <ThemeProvider theme={defaultTheme} >
          <Box sx={{ display: "flex" }} dir="rtl">
            <CssBaseline />
            <Drawer variant="permanent" open={true}>
              <Toolbar
                sx={{
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "flex-end",
                  px: [1],
                }}
              >

              </Toolbar>
              <Divider />
              <List component="nav">
                <React.Fragment>
                  <Link to="/users">
                    <ListItemButton Navigate>
                      <ListItemIcon>
                        <DashboardIcon />
                      </ListItemIcon>
                      <ListItemText primary="تهيئة ثوابت النظام" />
                    </ListItemButton>
                  </Link>
                  <Link text to="/users">

                    <ListItemButton onClick={SystemConstants}>
                      <ListItemIcon>
                        <ShoppingCartIcon />
                      </ListItemIcon>
                      <ListItemText primary="إدارة المستخدمين" />
                    </ListItemButton>
                  </Link>
                    <ListItemButton>
                      <ListItemIcon>
                        <PeopleIcon />
                      </ListItemIcon>
                      <ListItemText primary="وظائف أعضاء اللجان" />
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemIcon>
                        <BarChartIcon />
                      </ListItemIcon>
                      <ListItemText primary="الأعضاء" />
                    </ListItemButton>
                    <ListItemButton>
                      <ListItemIcon>
                        <LayersIcon />
                      </ListItemIcon>
                      <ListItemText primary="اللجان" />
                    </ListItemButton>
                </React.Fragment>

              </List>
            </Drawer>
            <AppBar
              position="absolute"
              sx={{ backgroundColor: "#E69081" }}
            >
              <Toolbar
                sx={{
                  pr: "24px", // keep right padding when drawer closed
                }}
              >
                <IconButton
                  edge="start"
                  color="inherit"
                  aria-label="open drawer"
                  sx={{
                    marginRight: "36px",
                    // ...(open && { display: "none" }),
                  }}
                >
                  <MenuIcon />
                </IconButton>

              </Toolbar>
            </AppBar>
            <Box
              component="main"
              sx={{
                backgroundColor: (theme) =>
                  theme.palette.mode === "light"
                    ? theme.palette.grey[100]
                    : theme.palette.grey[900],
                flexGrow: 1,
                height: "100vh",
                overflow: "auto",
              }}
            >
              <Toolbar />
              <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
                <Grid container spacing={3}>
                  {/* Chart */}

                  {/* Recent Deposits */}

                  {/* Recent Orders */}
                  <Grid item xs={12}>
                    <Paper sx={{ p: 2, display: "flex", flexDirection: "column" }}>

                      <Routes>
                        <Route path="/" element={<Login></Login>}></Route>
                        <Route path="/dashboard" element={<Dashboard></Dashboard>}></Route>
                        <Route path="/users" element={<Users></Users>}></Route>

                      </Routes>

                    </Paper>
                  </Grid>

                </Grid>


              </Container>
            </Box>
          </Box>
        </ThemeProvider>
      </BrowserRouter>



    </div>
  );
}

export default App;
