import * as React from "react";
import Link from "@mui/material/Link";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Title from "./Title";
import axios from "axios";
import { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as accountActions from "../../redux/actions/accountActions";
import Button from "@mui/material/Button";

function preventDefault(event) {
  event.preventDefault();
}

function Accounts(props) {
  const [isFormOpen, setIsFormOpen] = useState(false);

  const handleSaveUser = (accountInfo) => {
    console.log("Yeni hesap bilgileri:", accountInfo);
  };


  useEffect(() => {
    props.actions.getAccounts();
  }, [props.actions]);

  function dateFormatter(dateArray) {
    const year = dateArray[0];
    const month = dateArray[1];
    const day = dateArray[2];

    return year + " : " + month + " : " + day;
  }


  const navigate = useNavigate();

    const [employee, setEmployee] = useState([])

    useEffect(() => {
        getAllEmployee()
    }, [])

    function getAllEmployee() {
        listEmployees().then((response) => {
            setEmployee(response.data)
        }).catch(error => {
            console.error(error);
        })
    }
  return (
    <React.Fragment>
      <Title>المستخدمين {props.currentAccount.account_name}</Title>
      <Table size="small">
        <TableHead>
          <TableRow>
            <TableCell>Account Id</TableCell>
            <TableCell>Account Name</TableCell>
            <TableCell>Account Number</TableCell>
            <TableCell>Account Type</TableCell>
            <TableCell>User ID</TableCell>
            <TableCell>Updated At</TableCell>
            <TableCell>Account Balance</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {props.accounts.map((account) => (
            <TableRow
              onClick={() => {
                props.actions.changeAccount(account);
              }}
              key={account.account_id}
            >
              {
                 employee.map(item =>
                  <tr key={item.id} className='text-center'>
                      <td>{item.id}</td>
                      <td>{item.firstName}</td>
                      <td>{item.lastName}</td>
                      <td>{item.email}</td>
                      <td><button className='btn btn-success' onClick={() => updatehandler(item.id)}>Update</button></td>
                      <td><button className='btn btn-primary' onClick={() => deletehandler(item.id)}>Delete</button></td>
                  </tr>
              )
              }
              <TableCell>{account.account_id}</TableCell>
              <TableCell>{account.account_name}</TableCell>
              <TableCell>{account.account_number}</TableCell>
              <TableCell>{account.account_type}</TableCell>
              <TableCell>{account.user_id}</TableCell>
              <TableCell>{dateFormatter(account.updated_at)}</TableCell>
              <TableCell>{`$${account.balance}`}</TableCell>
              <TableCell align="right">
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => setIsFormOpen(true)}
                  sx={{ backgroundColor: "#F5BD52" }}
                >
                  Transact
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>

      <Link color="primary" href="#" onClick={preventDefault} sx={{ mt: 3 }}>
        رؤية المزيد من المستخدمين
      </Link>
    </React.Fragment>
  );
}

function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getAccounts: bindActionCreators(accountActions.getAccounts, dispatch),
      changeAccount: bindActionCreators(accountActions.changeAccount, dispatch),
    },
  };
}

function mapStateToProps(state) {
  return {
    currentAccount: state.changeAccountReducer,
    accounts: state.accountListReducer,
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Accounts);
