import React, { useState } from "react";
import Button from "@mui/material/Button";
import Drawer from "@mui/material/Drawer";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import alertify from "alertifyjs";
import axios from "axios";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { useNavigate, useParams } from 'react-router-dom'
import { useEffect } from 'react'
import { savedUser, updateDataUser, editUser } from '../service/UserService'

function AccountForm({ onSaveAccount, open = true, onClose, actions }) {


  const [firstName, setFirstName] = useState('')
  const [lastName, setLastName] = useState('')
  const [email, setEmail] = useState('')

  const navigate = useNavigate()
  const { id } = useParams()


  function pageTitle() {
    if (id) {
      return <h4 className='title'>Update Users</h4>
    } else {
      return <h4 className='title'>Add Users</h4>
    }
  }

  useEffect(() => {
    if (id) {
      editUser(id).then((response) => {
        setFirstName(response.data.firstName);
        setLastName(response.data.lastName);
        setEmail(response.data.email);
      })
    }
  }, [id])

  function saveUser(e) {
    e.preventDefault()
    alertify.success(" start save.");

    const user = { firstName, lastName, email }
    alertify.success(user.firstName);
    alertify.success(" succesfull.");

    alertify.success(user.email);


    if (firstName === "" || lastName === "" || email === "") {
      return;
    }
    if (id) {
      updateDataU(id, user).then((response) => {
        // navigate('/')
      }).catch(error => {
        console.error(error);
      })
    } else {
      savedUser(user).then((response) => {
      }).catch(error => {
        alertify.success(error.stack);
        alertify.success(error.message);

        console.error(error);
      })
      // navigate("/")
    }
  }

  return (
    <Drawer
      anchor="left"
      open={open}
      onClose={onClose}
      ModalProps={{
        disableScrollLock: true,
      }}
    >
      <div dir="rtl"
        style={{
          width: "300px",
          padding: "16px",
        }}
        role="presentation"
        onClick={onClose}
        onKeyDown={onClose}
      >
        <TextField dir="rtl"
          style={{ marginTop: "60px" }}
          name="user_name"
          label="اسم المستخدم "
          type="text"
          placeholder='Enter FirstName'
          className='form-control'
          onChange={(e) => setFirstName(e.target.value)}
          id="validationCustom01"
          required
          fullWidth
          margin="normal"
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {

            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          name="full_name"
          label="الاسم الكامل"
          fullWidth
          onChange={(e) => setLastName(e.target.value)}
          margin="normal"
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          name="password"
          label="كلمة المرور"
          fullWidth
          margin="normal"
          onChange={(e)=>setEmail(e.target.value)}
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />
        <TextField dir="rtl"
          type="checkbox"
          name="status"
          label="الحالة"
          value="1"
          // onChange={handleInputChange}
          fullWidth
          onClick={(event) => {
            event.stopPropagation();
          }}
          onKeyDown={(event) => {
            event.stopPropagation();
          }}
        />


        <Box mt={2}>
          <Button
            variant="contained"
            color="primary"
            onClick={saveUser}
            sx={{ backgroundColor: "#EB3D13" }}>
            حفظ
          </Button>
        </Box>
      </div>
    </Drawer>
  );
}

export default connect(null)(AccountForm);
